DNA <- ''
test <- strsplit(DNA,split=NULL)[[1]]
test <- rev(test)
Complement = c()

for (i in 1:length(test)) {
  if (test[i] =='A') {
    Complement[i] = 'T'
  } else if (test[i] =='T') {
    Complement[i] = 'A'
  } else if (test[i] =='C') {
    Complement[i] = 'G'
  } else if (test[i] =='G') {
    Complement[i] = 'C'
  }
}

Complement <- paste(Complement, collapse = '')
print(Complement)
